package Interview_Question;import java.util.Scanner;/** * 约瑟夫环 * * 约瑟夫环是一个数学应用题：已知n个人（编号1、2、..n）围坐在一张圆桌周围。 * 从编号为1的人开始报数，数到m的那个人出列；他的下一个又从1开始报数，数到m的那个人又出列； * 依次规律重复下去，直到圆桌周围的人全部出列。请编写一个程序，给定n、m计算出列人员先后顺序。 */public class Joseph {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m = sc.nextInt();        int[] arr = new int[n];        for (int i=0; i<n; i++){            arr[i] = i+1;        }        int index = -1;        for (int i=n; i!=1; i--){            int step = m;            while (step>0){                index++;                if (index>=n){                    index=0;                }                if (arr[index]!=0){                    step--;                }            }            arr[index]=0;        }        for (int i : arr){            if (i!=0){                System.out.println(i);                break;            }        }    }}