package Interview_Question;import java.util.Scanner;/** * 求素数个数 * * 吉比特笔试编程题 * * 输入M、N，1 < M < N < 1000000，求区间[M,N]内的所有素数的个数。素数定义：除了1以外，只能被1和自己整除的自然数称为素数 * 输入描述: * 两个整数M，N * * * 输出描述: * 区间内素数的个数 * * 输入例子1: * 2 10 * * 输出例子1: * 4 */public class PrimeNumber {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        long n = sc.nextLong();        long m = sc.nextLong();        int count = 0;        if (m<n){            System.out.println(0);            return;        }        if (n%2==0){            if (n==2){                count++;            }            n++;        }        for (long i=n; i<=m; i+=2){            boolean isPrimeNumber = true;            long k = (long) Math.sqrt(i);            for (;k>1; k--){                if (i%k==0){                    isPrimeNumber = false;                    break;                }            }            if (isPrimeNumber){                count++;            }        }        System.out.println(count);    }}